import React from "react";
import { connect } from "react-redux";
import { Link } from "react-router-dom";
import { Card } from "semantic-ui-react";
import { getProjects } from "../actions";

class ListProjects extends React.Component {
  componentDidMount() {
    console.log(this.props.projects);
  };

  renderList() {
    return this.props.projects.map(project => {
      return (
        <div className="item" key={project.id}>
          {/* {this.renderAdmin(stream)} */}
          <i className="large middle aligned icon fork" />
          <div className="content">
            <Link to={`/projects/${project.id}`} className="header">
              {project.project_name}
            </Link>
            <div className="description">{project.project_description}</div>
            <Card>
              <Card.Content>
                {/* <Card.Header>Steve Sanders</Card.Header>
                <Card.Meta>Friends of Elliot</Card.Meta> */}
                <Card.Description>
                  <div className="detail">
                    <div>Create Date: {project.create_date}</div>
                    <div>Due Date: {project.due_date}</div>
                    <div>Project Manager: {project.project_manager_id}</div>
                  </div>
                </Card.Description>
              </Card.Content>
            </Card>
          </div>
        </div>
      );
    });
  }

  render() {
    this.props.getProjects();
    return (
      <div>
        <h2>Projects</h2>
        <div className="ui celled list">{this.renderList()}</div>
      </div>
    );
  }
};

const mapStateToProps = (state) => {
  return { projects: Object.values(state.projects) }
};

const actions = {
  getProjects,
}

export default connect(mapStateToProps, actions)(ListProjects);
